import React from 'react';
import ReviewerData from './reviews.json';
import { render } from '@testing-library/react';




export class Test extends React.Component{
    render(){
        const student = "john"
        const course = "Psychology"
        const professor = "Mr. Larry"
        const review = "Cool class, lots to learn"

        return(
           
            <div>
            
                <Tester student = {student} course = {course} professor = {professor} review = {review} />

            </div>
        )
    }
}




export class Tester extends React.Component{
    render(){
        return(
            <div>
                <p>{this.props.student}</p>
                <p>{this.props.course}</p>
                <p>{this.props.professor}</p>
                <p>{this.props.review}</p>
                
            </div>
        )
    }
}



export class GiveReview extends React.Component{
    constructor(){
        super()
        this.state = {
            student : null,
            course : null,
            professor : null,
            review : null,
            comp: null
        }

 

    }

    operation(e){
        e.preventDefault()
        //var x = document.getElementById("reviews").value
        this.setState({
            student : null,
            course :  null,
            professor : null,
            reveiw :  null,
            comp: null
        })

        this.setState({
            comp: <Tester student = {this.state.student} course = {this.state.course} professor = {this.state.professor} review = {this.state.review} />
        })



    }


    addtoJSON(){
        var data = ReviewerData //parse the JSON
        data.push({       
            student: this.state.student, 
            course: this.state.course,
            professor: this.state.professor,
            review: this.state.review
        });

      

    }

    show(){
        document.getElementById('display').innerHTML = document.getElementById("user_input").value;
    }

    showone(){
        comp: <Tester student = {this.state.student} course = {this.state.course} professor = {this.state.professor} review = {this.state.review} />
                
    }

    render(){
        return(
            <div>

                <form>
                    <label><b>Enter a Message</b></label>
                    <input type="text" name="message" id="user_input"/>
                </form>

                <input type="submit" onclick={() => this.show()}/><br/>
                <label>Your input: </label>
                <p><span id='display'></span></p>


                <form>
                    <input 
                        placeholder = "Student Name"
                        type='text' 
                        value = {this.state.student}
                        onChange= {e => this.setState({student: e.target.value})}
                    />

                    <input 
                        placeholder = "Professor Name"
                        type='text' 
                        value = {this.state.professor}
                        onChange= {e => this.setState({professor: e.target.value})}
                    />

                    <input 
                        placeholder = "Course Name"
                        type='text' 
                        value = {this.state.course}
                        onChange= {e => this.setState({course: e.target.value})}
                    />

                    <input 
                        placeholder = "Course Review"
                        type='text' 
                        value = {this.state.review}
                        onChange= {e => this.setState({review: e.target.value})}
                    />
                </form>
                
                <input type="submit" onclick={() => this.showone()}/><br/>


       


            
                
                <form id = "reviews"> 

                    <input type = 'text' placeholder = "Student Name"></input>
                    
                    <input type = 'text' placeholder = "Professor Name"></input>

                    <input type = 'text' placeholder = "Course Name"></input>
                    
                    <input type = 'text' placeholder = "Course Review"></input>

                    <button onClick = {() => this.operation()}>Submit</button>

                </form>


                <p>
                    <Tester student = {this.state.student} course = {this.state.course} professor = {this.state.professor} review = {this.state.review} />
                </p>

               


            </div>
            
        )
    }


}
