import React from 'react';
import ReviewerData from './reviews.json';
import { render } from '@testing-library/react';




export class Test extends React.Component{
    render(){
        const student = "john"
        const course = "Psychology"
        const professor = "Mr. Larry"
        const review = "Cool class, lots to learn"

        return(
           
            <div>
            
                <Tester student = {student} course = {course} professor = {professor} review = {review} />

            </div>
        )
    }
}




export class Tester extends React.Component{
    render(){
        return(
            <div>
                <p>{this.props.student}</p>
                <p>{this.props.course}</p>
                <p>{this.props.professor}</p>
                <p>{this.props.review}</p>
                
            </div>
        )
    }
}



export class GiveReview extends React.Component{
    constructor(){
        super()
        this.state = {
            student : null,
            course : null,
            professor : null,
            review : null
        }
        this.handleStudentChange = this.handleStudentChange.bind(this);
        this.handleCourseChange = this.handleCourseChange.bind(this);
        this.handleProfessorChange = this.handleProfessorChange.bind(this);
        this.handleReviewChange=this.handleReviewChange.bind(this);

    }

    operation(e){
        e.preventDefault()
        
        this.setState({
            student : document.getElementById("student").value,
            course :  document.getElementById("course").value,
            professor :  document.getElementById("professor").value,
            reveiw :  document.getElementById("review").value,
            comp: null
        })

        this.setState({
            comp: <Tester student = {this.state.student} course = {this.state.course} professor = {this.state.professor} review = {this.state.review} />
        })



    }
    handleDisplay() {
        alert("Data: " + this.state.student + this.state.course + this.state.professor + this.state.review);
    }
    handleStudentChange(e) {
        
        this.setState({student: e.target.value});
     }
     handleCourseChange(e) {
        
        this.setState({course: e.target.value});
     }
     handleProfessorChange(e) {
        
        this.setState({professor: e.target.value});
     }
     handleReviewChange(e) {
        
        this.setState({review: e.target.value});
     }


    render(){
        return(
            <div>
                <form>
                    <input name = "student" type = 'text' placeholder = "Student Name" value={this.state.student} onChange = {this.handleStudentChange}></input>
                    
                    <input name = "course" type = 'text' placeholder = "Professor Name" value = {this.state.course} onChange = {this.handleCourseChange}></input>

                    <input name = "professor" type = 'text' placeholder = "Course Name" value = {this.state.professor} onChange= {this.handleProfessorChange}></input>
                    
                    <input name = "review" type = 'text' placeholder = "Course Review" value= {this.state.review} onChange = {this.handleReviewChange} ></input>

                    <button type = 'submit' onClick = {this.handleDisplay}>Submit</button>
                </form>

               


            </div>
            
        )
    }


}
