import React from 'react';




export class Review extends React.Component{

    constructor(props){
        super(props)
        this.state = {

        }

    }


    render(){
        return(
            <div>
                <p>{this.props.course}</p>
                <p>{this.props.student}</p>
                <p>{this.props.professor}</p>
                <p>{this.props.review}</p>

            </div>
            
        )
    }


}


export class Reviewer extends React.Component{
    constructor(){
        super()
        this.state = {
            comp : null

        }

    }

    operation(e){
        
        this.setState({
            comp: <Review student = "john" course = "Psychology" professor = "Mr. Larry" review = "easy class with no homework"/>
        })

    }


    render(){
        return(
            <div>
                <form>
                    <input type = 'text' placeholder = "Student Name"></input>
                    
                    <input type = 'text' placeholder = "Professor Name"></input>

                    <input type = 'text' placeholder = "Course Name"></input>
                    
                    <input type = 'text' placeholder = "Course Review"></input>

                    <button type = 'submit' onClick = {(e) => this.operation()}>Submit</button>
                </form>

                <p>{this.state.comp}</p>



            </div>
            
        )
    }


}
